import './background.css'

export default function Background() {
  let cvs = (
    <canvas id="background-canvas">
    </canvas>
  ),
  img = <div id="background-image" width={window.innerWidth}></div>
  return (
    <div>
      {img}
    </div>
  )
}


function createBackground() {
  //配置
  const count = 320, //点数量
    speed = 1, //点平均速度
    dist = 60, //点间成线最大距离
    image = "../../src/background.png", //图片地址
    rSpeed = 0.05, //图片旋转速度
    rMax = 80, //图片旋转最大角度
    frame = 20 //帧率

  var cvs = document.createElement("canvas"),
    div = document.createElement("div"),
    ctx = cvs.getContext("2d"),
    w = cvs.width = window.innerWidth,
    h = cvs.height = window.innerHeight,
    r = 0,
    rVel = rSpeed,
    points = []
  cvs.id = "background-canvas"
  div.id = "background-image"
  div.style.backgroundImage = "url('" + image + "')"
  document.body.appendChild(cvs)
  document.body.appendChild(div)
  for (var i = 0; i < count; ++i) {
    points.push({
      size: Math.random() * 4 + 0.5,
      pos: {
        x: Math.random() * w,
        y: Math.random() * h
      },
      vel: {
        x: (Math.random() - 0.5) * speed,
        y: (Math.random() - 0.5) * speed
      },
      color: {
        r: Math.random() * 255,
        g: Math.random() * 255,
        b: Math.random() * 255
      }
    })
  }

  setInterval(() => {
    w = cvs.width = window.innerWidth
    h = cvs.height = window.innerHeight
    r += rVel
    if ((r > rMax && rVel > 0) || (r < -rMax && rVel < 0)) rVel *= -1
    div.style.transform = "rotate(" + r + "deg)"
    ctx.clearRect(0, 0, w, h)
    points.forEach((p, idx) => {
      p.pos.x += p.vel.x
      p.pos.y += p.vel.y
      if ((p.pos.x <= 0 && p.vel.x < 0) || (p.pos.x >= w && p.vel.x > 0)) p.vel.x *= -1
      if ((p.pos.y <= 0 && p.vel.y < 0) || (p.pos.y >= h && p.vel.y > 0)) p.vel.y *= -1
      ctx.fillStyle = "white"
      ctx.fillRect(p.pos.x - p.size / 2, p.pos.y - p.size / 2, p.size, p.size)
      for (var i = idx + 1; i < points.length; ++i) {
        var pi = points[i],
          xDist = pi.pos.x - p.pos.x,
          yDist = pi.pos.y - p.pos.y,
          rate = 1 - (xDist * xDist + yDist * yDist) / (dist * dist)
        if (rate > 0) {
          ctx.lineWidth = 0.4
          ctx.strokeStyle = "rgba(" + p.color.r + "," + p.color.g + "," + p.color.b + "," + rate + ")"
          ctx.beginPath()
          ctx.moveTo(p.pos.x, p.pos.y)
          ctx.lineTo(pi.pos.x, pi.pos.y)
          ctx.stroke()
        }
      }
    })
  }, 1000 / frame)
}
