import { useNavigate } from 'react-router-dom'
import './menu-bar.css'

export default function MenuBar(props) {
  const menuList = [
    { text: "首页", url: "/home" },
    { text: "订单", url: "/order" },
    { text: "统计", url: "/stat" },
    { text: "购物车", url: "/cart" },
    { text: "帐户", url: "/account" }
  ],
    navigate = useNavigate()

  return (
    <div id='MenuBar'>
      <h1 id='MenuBar-title'>星空书店</h1>
      <ul id='MenuBar-menu'>
        {
          menuList.map((item, index) =>
            <li
              className={index === props.index ? 'MenuBar-menuList MenuBar-menuList-active' : 'MenuBar-menuList'}
              onClick={() => navigate(item.url)}
            >{item.text}</li>
          )
        }
      </ul>
      <button
        id="MenuBar-cart"
        onClick={() => navigate('/cart')}
      ></button>
      <button
        id="MenuBar-account"
        onClick={() => navigate('/account')}
      ></button>
    </div>
  )
}
