import { apiURL, post, get } from './util'

export async function getCart() {
  let res = await get(apiURL + '/cart'),
    cartList = []
  res.forEach(item => {
    let book = item.book
    cartList.push({
      itemId: item.id,
      cover: book.cover,
      title: book.title,
      author: book.author,
      price: book.price,
      number: item.number
    })
  })
  return cartList
}
