export default function MenuBar() {
  const menuList = [
    {
      text: "首页",
      url: "/home"
    }, {
      text: "购物车",
      url: ""
    }, {
      text: "订单",
      url: ""
    }, {
      text: "统计",
      url: ""
    }, {
      text: "账户",
      url: ""
    }
  ]

let ul = document.createElement("ul"),
  title = document.createElement("li")
document.body.appendChild(ul)
ul.appendChild(title)
ul.id = "menu-bar-ul"
title.id = "menu-bar-title"
title.innerHTML = "星空书店"
for (let i in menuList) {
  let li = document.createElement("li")
  ul.appendChild(li)
  li.className = "menu-bar-li"
  li.onclick = () => { location.href = menuList[i].url }
  li.innerHTML = menuList[i].text
  if (i == index)
    li.classList.add("menu-bar-li-active")
}

  return (
    <div id="MenuBar">
      <h1 id="MenuBar-title">星空书店</h1>
      <ul id="MenuBar-menu">
      </ul>
    </div>
  )
}
