import MenuBar from '../components/menu-bar'
import SearchBar from '../components/search-bar'
import './home.css'

export default function HomePage() {
  const categoryList = [
    {
      text: "推荐"
    }, {
      text: "热门"
    }, {
      text: "文学"
    }, {
      text: "娱乐"
    }, {
      text: "专业"
    }, {
      text: "教育"
    }, {
      text: "科普"
    }
  ]

  document.body.style.overflow = 'auto'

  return (
    <div>
      <MenuBar index={0} />
      <SearchBar placeholder='请输入书籍名、作者名等关键词搜索相关书籍' />
      <div id='BookDisplay'>
        <ul id='BookDisplay-category'>
          {
            categoryList.map((item, index) =>
              <li
                key={index}
                className={index === 0 ? 'BookDisplay-categoryList BookDisplay-categoryList-cative' : 'BookDisplay-categoryList'}
              >{item.text}</li>
            )
          }
        </ul>
      </div>
    </div>
  )
}
