export const apiURL = "https://10.119.12.209:8080"

export async function post(url, data) {
  let res = await fetch(url, {
    method: "POST",
    body: JSON.stringify(data),
    headers: { 'Content-Type': 'application/json' },
    credentials: "include"
  })
  if (!res.ok)
    throw '网络错误!'
  return res.json();
}
