import { useRef } from 'react'
import { useNavigate } from 'react-router-dom'
import { Checkbox } from 'antd';
import './login.css'

export default function LoginPage() {
  document.body.style.overflow = 'hidden'
  const navigate = useNavigate(),
    login = useRef(null),
    signup = useRef(null),
    shiftToSignup = () => {
      login.current.classList.remove("login-in")
      login.current.classList.add("login-out")
      signup.current.classList.remove("signup-out")
      signup.current.classList.add("signup-in")

      // let login = document.getElementById("login"),
      //   signup = document.getElementById("signup")
      // login.classList.remove("login-in")
      // login.classList.add("login-out")
      // signup.classList.remove("signup-out")
      // signup.classList.add("signup-in")
    },
    shiftToLogin = () => {
      let login = document.getElementById("login"),
        signup = document.getElementById("signup")
      signup.classList.remove("signup-in")
      signup.classList.add("signup-out")
      login.classList.remove("login-out")
      login.classList.add("login-in")
    }

  function onLogin() {
    navigate("/home")
  }

  function onSignup() {
    navigate("/home")
  }

  return (
    <div>
      <h1 id='title'>星空<br />&ensp;书店</h1>
      <h2 id='intro'>以最实惠的价格，<br />&nbsp;畅享书籍的浩瀚星空。</h2>

      <div ref={login} class="entry login-enter">
        <h1 class="entry-title">登 录</h1>
        <input class="entry-input" type="id" placeholder="账号" />
        <input class="entry-input" type="password" placeholder="密码" />
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-around', width: '100%', margin: '20px auto 10px' }}>
          <Checkbox><span style={{ color: 'white', fontSize: '12px' }}>记住我</span></Checkbox>
          <button id="forget">忘记密码？</button>
        </div>
        <button class="entry-submit" onClick={onLogin}>登 录</button>
        <button class="entry-shift" onClick={shiftToSignup}>没有账号？点我注册</button>
      </div>

      <div ref={signup} class="entry signup-enter">
        <h1 class="entry-title">注 册</h1>
        <input class="entry-input" type="id" placeholder="用户名" />
        <input class="entry-input" type="password" placeholder="密码" />
        <input class="entry-input" type="password" placeholder="确认密码" />
        <button class="entry-submit" onClick={onSignup}>注 册</button>
        <button class="entry-shift" onClick={shiftToLogin}>已有账号？点我登录</button>
      </div>
    </div >
  )
}
