import { apiURL, post, get } from './util'

export async function getCart() {
  let res = await get(`apiURL/cart`),
    cartList = []
  res.forEach(item => {
    let book = item.book
    cartList.push({
      itemId: item.id,
      cover: book.cover,
      title: book.title,
      author: book.author,
      price: book.price,
      number: item.number
    })
  })
  return cartList
}

export async function addIntoCart(bookId) {
  let res = await post(`apiURL/cart?bookId=${bookId}`)
  if (res.ok) return true
  else throw res.message
}
