import { useRef } from 'react'
import { useNavigate } from 'react-router-dom'
import { Checkbox } from 'antd'
import '../css/login.css'
import { login } from '../service/login'

export default function LoginPage() {
  document.body.style.overflow = 'hidden'
  const navigate = useNavigate(),
    login = useRef(null),
    signup = useRef(null),
    login_id = useRef(null),
    login_pwd = useRef(null),
    shiftToSignup = () => {
      let loginClass = login.current.classList,
        signupClass = signup.current.classList
      loginClass.remove('login-in')
      loginClass.add('login-out')
      signupClass.remove('signup-out')
      signupClass.add('signup-in')
    },
    shiftToLogin = () => {
      let loginClass = login.current.classList,
        signupClass = signup.current.classList
      loginClass.remove('login-out')
      loginClass.add('login-in')
      signupClass.remove('signup-in')
      signupClass.add('signup-out')
    },
    onLoginIdKeyUp = (e) => {
      // if (e.key === 'Enter' && login_id.current.value !== '') 
      //   login_pwd.current.focus()
      if (e.key === 'Down')
        login_pwd.current.focus()
    },
    onLogin = () => {
      if (login_id.current.value === '') {
        login_id.current.focus()
        return
      } else if (login_pwd.current.value === '') {
        login_pwd.current.focus()
        return
      }
      navigate("/home")
    },
    onSignup = () => {
      navigate("/home")
    }

  return (
    <div>
      <h1 id='title'>星空<br />&ensp;书店</h1>
      <h2 id='intro'>以最实惠的价格，<br />&nbsp;畅享书籍的浩瀚星空。</h2>

      <div class="entry login-enter">
        <h1 class="entry-title">登 录</h1>
        <input ref={login_id} class="entry-input" type="id" placeholder="账号" onKeyUp={onLoginIdKeyUp}/>
        <input ref={login_pwd} class="entry-input" type="password" placeholder="密码" />
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-around', width: '100%', margin: '20px auto 10px' }}>
          <Checkbox><span style={{ color: 'white', fontSize: '12px' }}>记住我</span></Checkbox>
          <button id="forget">忘记密码？</button>
        </div>
        <button class="entry-submit" onClick={onLogin}>登 录</button>
        <button class="entry-shift" onClick={shiftToSignup}>没有账号？点我注册</button>
      </div>

      <div class="entry signup-enter">
        <h1 class="entry-title">注 册</h1>
        <input class="entry-input" type="id" placeholder="用户名" />
        <input class="entry-input" type="password" placeholder="密码" />
        <input class="entry-input" type="password" placeholder="确认密码" />
        <button class="entry-submit" onClick={onSignup}>注 册</button>
        <button class="entry-shift" onClick={shiftToLogin}>已有账号？点我登录</button>
      </div>
    </div>
  )
}
