import { useNavigate } from 'react-router-dom'
import './menu-bar.css'

export default function MenuBar(props) {
  const menuList = [
    { text: "首页", url: "/home" },
    { text: "订单", url: "/order" },
    { text: "统计", url: "/stat" },
  ]
  const navigate = useNavigate()

  return (
    <div id='MenuBar'>
      <h1 id='MenuBar-title'>星空书店</h1>
      <ul id='MenuBar-menu'>
        {
          menuList.map((item, index) =>
            <li
              className={index === props.index ? 'MenuBar-menuList MenuBar-menuList-active' : 'MenuBar-menuList'}
              onClick={() => navigate(item.url)}
            >{item.text}</li>
          )
        }
        <li
          id="MenuBar-cart"
          className={props.index === menuList.length ? 'MenuBar-menuList-active' : ''}
          onClick={() => navigate('/cart')}
        ></li>
        <li
          id="MenuBar-account"
          className={props.index === menuList.length + 1 ? 'MenuBar-menuList-active' : ''}
          onClick={() => navigate('/account')}
        ></li>
      </ul>

    </div>
  )
}
