import './menu-bar.css'

export default function MenuBar(props) {
  const menuList = [
    { text: "首页", url: "/home" },
    { text: "订单", url: "/order" },
    { text: "统计", url: "/stat" },
    { text: "购物车", url: "/cart" },
    { text: "帐户", url: "/account" }
  ]
