//   li.className = "menu-bar-li"
//   li.onclick = () => { location.href = menuList[i].url }
//   li.innerHTML = menuList[i].text
//   if (i == index)
//     li.classList.add("menu-bar-li-active")
// }

  return (
    <div id="MenuBar">
      <h1 id="MenuBar-title">星空书店</h1>
      <ul id="MenuBar-menu">
        { menuList.map((item, index) => <li>item.text</li>) }
      </ul>
    </div>
  )
}
