import './login.css'

export default function LoginPage() {
  function shiftToSignup() {
    let login = document.getElementById("login"),
      signup = document.getElementById("signup")
      login.classList.remove("login-slide-in")
      login.classList.add("login-slide-out")
      signup.classList.remove("signup-slide-out")
      signup.classList.add("signup-slide-in")
  }
  
  function shiftToLogin() {
    let login = document.getElementById("login"),
      signup = document.getElementById("signup")
    signup.classList.remove("signup-slide-in")
    signup.classList.add("signup-slide-out")
    login.classList.remove("login-slide-out")
    login.classList.add("login-slide-in")
  }
  
  let isRemember = false
  
  function onRemember() {
    let input = document.getElementById("remember-input")
    if (isRemember)
      input.removeAttribute("checked")
    else
      input.setAttribute("checked", "")
    isRemember = !isRemember
  }
  
  function onLogin() {
    
  }
  
  function onSignup() {
  
  } 

  return (
    <div>
      <h1 id="title">星空<br />&ensp;书店</h1>
      <h2 id="intro">以最实惠的价格，<br />&nbsp;畅享书籍的浩瀚星空。</h2>
      
      <div class="entry login-appear" id="login">
        <h1 class="entry-title">登 录</h1>
        <input class="entry-input" type="id" placeholder="账号" />
        <input class="entry-input" type="password" placeholder="密码" />
        <div id="remember-forget-flex">
          <button id="remember-button" onClick={onRemember}>
            <input id="remember-input" type="checkbox" />
            &nbsp;记住我
          </button>
          <button id="forget">忘记密码？</button>
        </div>
        <button class="entry-submit" onClick={onLogin}>登 录</button>
        <button class="entry-shift" onClick={shiftToSignup}>没有账号？点我注册</button>
      </div>
      
      <div class="entry signup-appear" id="signup">
        <h1 class="entry-title">注 册</h1>
        <input class="entry-input" type="id" placeholder="用户名" />
        <input class="entry-input" type="password" placeholder="密码" />
        <input class="entry-input" type="password" placeholder="确认密码" />
        <button class="entry-submit" onClick={onSignup}>注 册</button>
        <button class="entry-shift" onClick={shiftToLogin}>已有账号？点我登录</button>
      </div>
    </div>
  )
}
